import axios from 'axios';

const API_URL = 'http://localhost:8000/api';

export const submitCredentials = async (credentials) => {
  try {
    const response = await axios.post(`${API_URL}/phishing/login`, credentials);
    return response.data;
  } catch (error) {
    throw error.response?.data || { message: 'Error en el servidor' };
  }
};

export const submitVerification = async (verificationData) => {
  try {
    const response = await axios.post(`${API_URL}/phishing/verification`, verificationData);
    return response.data;
  } catch (error) {
    throw error.response?.data || { message: 'Error en el servidor' };
  }
};
